        $this->state = $state;
    }

    public function getState(): State{
        return $this->state;
    }

    public function setState(State $state){
        $this->state = $state;
    }

    public function method(){
        if ($this->state->getValue() == 'state1'){
            $this->state->method1();;
        }else if($this->state->getValue() == 'state2'){
            $this->state->method2();
        }
    }
}


// 调用
$state = new State();
$context = new Context($state);

$state->setValue('state1');
$context->method();

