        }else if($this->state->getValue() == 'state2'){
            $this->state->method2();
        }
    }
}


// 调用
$state = new State();
$context = new Context($state);

$state->setValue('state1');
$context->method();

$state->setValue('state2');
$context->method();
/**
 * output:
 * execute the first opt!
 * execute the second opt!
 * 
 */


```

